import { Component } from '@angular/core';

type Item = {
  quantity: any; name: string; price: number 
};

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'FoodCart';

  items: Item[] = [];

  newItemName = '';
  newItemPrice = 0;
  disable = false;
  quantity =0;

  addItem() {
    if (this.newItemName && this.newItemPrice) {
      this.items = [...this.items, { name: this.newItemName, price: this.newItemPrice, quantity: 0 }];
      this.newItemName = '';
      this.newItemPrice = 0;
    }
  }

  deleteItem(item: Item) {
    this.items = this.items.filter(i => i !== item);
  }

  increment(item: Item) {
    item.quantity++;
  }

  decrement(item: Item) {
    if (item.quantity > 0) {
      item.quantity--;
    }
  }

  get grandTotal() {
    return this.items.reduce((total, item) => total + item.price * item.quantity, 0);
  }
}
