import { Component } from '@angular/core';

type Item = {
  quantity: any; name: string; price: number 
};

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'FoodCart';

  items: Item[] = [];

  newItemName = '';
  newItemPrice = 0;
  quantity=0;
  // totalprice=0 ;
  newItemQuantity = 0

  addItem() {
    if (this.newItemName && this.newItemPrice) {
      const newItem: Item = { name: this.newItemName, price: this.newItemPrice, quantity: this.newItemQuantity };
      this.items = [...this.items, newItem];
       this.newItemPrice = 0;
       this.newItemName = '';
       this.newItemPrice = 0;
       this.newItemQuantity = 0;

      // const sum= this.newItemPrice * this.quantity; 
      // this.totalprice=this.totalprice+sum;
    }
  }

  deleteItem(item: Item) {
    this.items = this.items.filter(i => i !== item);
  }
  incrementQuantity(item: Item) {
    item.quantity++;
    }

    decrementQuantity(item: Item) {
    if (item.quantity > 0) {
    item.quantity--;
    }
    }

  get grandTotal() {
    return this.items.reduce((total, item) => total + item.price*this.quantity, 0);
  }
}
